#include <iostream>
#include <string>
using namespace std;

bool isLowercaseOnly(const string& s) {
    for (char c : s) {
        if (c < 'a' || c > 'z') {
            return false;
        }
    }
    return true;
}

int countVowels(const string& s) {
    int count = 0;
    for (char c : s) {
        if (c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u') {
            count++;
        }
    }
    return count;
}

int main() {
    string s;
    cin >> s;

    if (!isLowercaseOnly(s)) {
        cout << "Invalid!" << endl;
    } else {
        cout << countVowels(s) << endl;
    }

    return 0;
}

