#include <iostream>
using namespace std;

int main() {
    int n;
    cin >> n;

    if (n == 0) {
        cout << "No odd number found" << endl;
        return 0;
    }

    int num;
    bool found = false;

    for (int i = 0; i < n; i++) {
        cin >> num;
        if (num % 2 != 0) {
            cout << num << endl;
            found = true;
            break;
        }
    }

    if (!found) {
        cout << "No odd number found" << endl;
    }

    return 0;
}

