using namespace std;

int main() {
    int n;
    cin >> n;

    if (n == 0) {
        cout << "No odd number found" << endl;
        return 0;
    }

    int x;
    bool found = false;

    for (int i = 0; i < n; i++) {
        cin >> x;
        if (!found && x % 2 != 0) {
            cout << x << endl;
            found = true;
        }
    }

    if (!found) {
        cout << "No odd number found" << endl;
    }

    return 0;
}

