#include <iostream>
#include <string>
using namespace std;

int main() {
    string s;
    cin >> s;

    for (char c : s) {
        if (c < 'a' || c > 'z') {
            cout << "Invalid!" << endl;
            return 0;
        }
    }

    bool same = true;
    int n = s.length();

    for (int i = 0; i < n / 2; i++) {
        if (s[i] != s[n - 1 - i]) {
            same = false;
            break;
        }
    }

    if (same)
        cout << "Yes" << endl;
    else
        cout << "No" << endl;

    return 0;
}

